// Simple addition
export function addThis(a, b) {
    var val1 = a;
    var val2 = b;

    return val1 + val2;
}

// Simple subtraction
export function subtractThis(a, b) {
    var val1 = a;
    var val2 = b;

    return val1 - val2;
}

// Simple Multiplication
export function multiplyThis(a, b) {
    var val1 = a;
    var val2 = b;

    return val1 * val2;
}

// Simple Division
export function divideThis(a, b) {
    var val1 = a;
    var val2 = b;

    return val1 / val2;
}


